import {createSlice} from "@reduxjs/toolkit"
const shoppingCartInitialState={
    items:[],
    isVisible:false
}
const shoppingCartSlice=createSlice({
    name:"cart",
    initialState:shoppingCartInitialState,
    reducers:{
        toggleCart(state){
            state.isVisible=!state.isVisible
        },
        addToCart(state,action){
            state.items=[...state.items,{title:action.payload.title,price:action.payload.price,total:action.payload.total,quantity:1}]
        },
        increaseAmount(state,action){
           const updatedData=state.items.map(item =>{
                if(item.title===action.payload.title){
                    return
                }
           })
        },
        // decreaseAmount(state,action){
        //     if(state.items[action.payload.id].quantity===1){
        //         state.items=state.items.filter(item=>item.name!==)
        //     }
        // }
    }
})
export default shoppingCartSlice.reducer
export const cartActions=shoppingCartSlice.actions;
