//   margin: 0.5rem 0;

// & label {
//   font-weight: bold;
//   display: block;
//   margin-bottom: 0.5rem;
// }

// & input {
//   display: block;
//   width: 100%;
//   border: 1px solid ${props => props.invalid?"salmon":"#ccc"};
//   font: inherit;
//   line-height: 1.5rem;
//   padding: 0 0.25rem;
//   background: ${props => props.invalid?"rgb(233, 121, 121);":""}; 
// }

// & input:focus {
