        const userInput=[
            {"Username":username}
        ]
        props.addUserHandler();
    }
    return(
        <form className={styles.input}>
            <div className={styles.input}>
                <label htmlFor="username" className={styles.label}>Username</label>
                <input onBlur={(e)=>setUsername(e.target.value)}  type="text" id="username" className={styles.input} />
            </div>
            <div className={styles.input}>
                <label htmlFor="username" className={styles.label}>Age (Years)</label>
                <input onBlur={(e)=>setAge(e.target.value)} type="number" id="username" className={styles.input} />
            </div>
            <div className={styles.input}>
                <Button type="submit" onClick={(e)=>props.onUserAdd(e)} children={"Add User"} />
            </div>
        </form>
    );
}
